	/**
	 * Returns prediction accuracy of prediction model
	 * 
	 * @param num
	 * @return
	 */
	static double getACC(int num) {
		if (num == 0)
			return 99.72;
		else if (num == 1)
			return 98.62;
		else if (num == 2)
			return 97.66;
		else if (num == 3)
			return 96.73;
		else if (num == 4)
			return 98.35;
		else if (num == 5)
			return 97.51;
		else if (num == 6)
			return 97.48;
		else if (num == 7)
			return 96.57;
		else if (num == 8)
			return 98.21;
		else if (num == 9)
			return 97.26;
		else if (num == 10)
			return 97.99;
		else if (num == 11)
			return 98.29;
		else
			return 99.72;
		
	}
	
	/**
	 * Returns the accuracy of zoonotic prediction models
	 * 
	 * @param num corresponding to model (ANN, IBk, Naive Bayes, RF, SVM)
	 * @return final model accuracy
	 */
	static double getZoonoticACC (int num) {
		if (num == 0)
			return 0.9441; // ANN
		else if (num == 1)
			return 0.9472; // IBk
		else if (num == 2)
			return 0.9379; // Naive Bayes
		else if (num == 4)
			return 0.9565; // Random Forest
		else
			return 0.9596; // SVM
	}
	
	/**
	 * Returns the css td style according to prediction result
	 * 
	 * @param predictionResult
	 * @return
	 */
	static String getSigStyle(int predictionResult) {
		if (predictionResult == 0)
			return "aviancolumn";
		else if (predictionResult == 1)
			return "humancolumn";
		else
			return "nullcolumn";
	}
	
	/**
	 * Returns true if array has complete host tropism protein signature
	 * 
	 * @param sigArr
	 * @return
	 */
	static boolean hasCompleteSig(int[] sigArr) {
		for (int i = 0; i < sigArr.length; i++) {
			if (sigArr[i] != 0 && sigArr[i] != 1)
				return false;
		}
		
		return true;
	}
